const nmapOutput = $input.first().json.stdout;

function parseNmapXML(xmlData) {
  const hosts = [];
  const hostRegex = /<host[^>]*>([\s\S]*?)<\/host>/g;
  let hostMatch;

  while ((hostMatch = hostRegex.exec(xmlData)) !== null) {
    const hostBlock = hostMatch[1];
    const stateMatch = /<status\s+state="([^"]+)"/.exec(hostBlock);
    if (!stateMatch || stateMatch[1] !== 'up') continue;

    const addressMatch = /<address\s+addr="([^"]+)"\s+addrtype="ipv4"/.exec(hostBlock);
    const ip = addressMatch ? addressMatch[1] : 'unknown';

    const ports = [];
    const portRegex = /<port\s+protocol="([^"]+)"\s+portid="([^"]+)">([\s\S]*?)<\/port>/g;
    let portMatch;

    while ((portMatch = portRegex.exec(hostBlock)) !== null) {
      const protocol = portMatch[1];
      const portid = portMatch[2];
      const portBlock = portMatch[3];

      const portStateMatch = /<state\s+state="([^"]+)"/.exec(portBlock);
      const state = portStateMatch ? portStateMatch[1] : 'unknown';

      const serviceMatch = /<service\s+name="([^"]+)"(?:\s+product="([^"]*)")? ?\/?>/.exec(portBlock);
      const service = serviceMatch ? serviceMatch[1] : 'unknown';
      const product = serviceMatch && serviceMatch[2] ? serviceMatch[2] : '';

      // ìœ„í—˜ë„ í‰ê°€
      const criticalPorts = ['22', '23', '3389', '5900', '445'];
      const dbPorts = ['3306', '5432', '1433', '27017', '6379'];
      let risk_level = 'low';

      if (criticalPorts.includes(portid)) risk_level = 'critical';
      else if (dbPorts.includes(portid)) risk_level = 'high';
      else if (['80', '443', '8080', '8443'].includes(portid)) risk_level = 'medium';

      ports.push({
        port: parseInt(portid),
        protocol,
        state,
        service,
        product,
        risk_level
      });
    }

    hosts.push({
      ip,
      state: 'up',
      ports,
      open_ports_count: ports.filter(p => p.state === 'open').length
    });
  }

  return hosts;
}

function generateStatistics(hosts) {
  let stats = {
    total_hosts: hosts.length,
    total_ports: 0,
    open_ports: 0,
    critical_ports: 0,
    high_ports: 0,
    medium_ports: 0
  };

  hosts.forEach(host => {
    host.ports.forEach(port => {
      stats.total_ports++;
      if (port.state === 'open') {
        stats.open_ports++;
        if (port.risk_level === 'critical') stats.critical_ports++;
        else if (port.risk_level === 'high') stats.high_ports++;
        else if (port.risk_level === 'medium') stats.medium_ports++;
      }
    });
  });

  return stats;
}

